    const contrasennaReg = /^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{8,}$/;
    const correoReg = /^[a-z0-9]+@estudiantec.cr$/;
    const telefonoReg = /^(2|6|8){1}[0-9]{7}$/;
    const cedulaReg = /^1{1}[0-9]{8}$/;
    const data = await Profesor.findOne({
      correo: DTOProfesor.correo
    });
